import com.eviware.soapui.support.XmlHolder
def response=context.testCase.getTestStepByName('Call CreateCustomer').getPropertyValue("response")
def holder=new XmlHolder(response)
def customerid= holder.getNodeValue("//*:customerId")
log.info customerid
context.testCase.setPropertyValue("Customer_ID",customerid)

import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status

def filepath = "C:/Users/RamaniPalaniappan/Desktop/SDET_B1_API/CustomerDetails.xls"

File excel = new File(filepath)

//to fetch workbook
Workbook wb = Workbook.getWorkbook(excel)

//to fetch sheet
Sheet sh=wb.getSheet("Sheet1")

//to fetch number of rows and columns
def rowsize = sh.getRows().toInteger()
def columnsize = sh.getColumns().toInteger()
log.info "$rowsize and $columnsize"

//to fetch the value in the variable
for(def i=1;i<rowsize;i++)
{
	for(def j=0;j<columnsize;j++)
	{
		if(j==0)
		{
			email = sh.getCell(j,i).getContents()
			log.info "$email"
		}
		else if(j==1)
		{
			firstname = sh.getCell(j,i).getContents()
			log.info "$firstname"
		}
		else if(j==2)
		{
			lastname = sh.getCell(j,i).getContents()
			log.info "$lastname"
		}
		else
		{
			dob = sh.getCell(j,i).getContents()
			log.info "$dob"
		}
	}

	// get TestCase
	def tc = testRunner.testCase.testSuite.project.testSuites["CustomerRepository Functional Testing"].testCases["CustomerScenarioEnd to End Flow"]

	// set credential properties from the script itself
	tc.testSteps["CustomerDetails"].setPropertyValue("email", email )
	tc.testSteps["CustomerDetails"].setPropertyValue("firstname", firstname )
	tc.testSteps["CustomerDetails"].setPropertyValue("lastname", lastname )
	tc.testSteps["CustomerDetails"].setPropertyValue("dob", dob )

	// run test synchrouously
	def runner = tc.run( null, false )

	// show the test case result status
	log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
	log.info "Number of test steps which ran: " + runner.results.size()

	// assert that it didn't fail
	assert runner.status != Status.FAILED : runner.reason

	// assert that only four steps ran!!
	//assert runner.results.size() == 5

}

import com.eviware.soapui.model.testsuite.TestRunner.Status

def res = context.testCase.getTestStepByName('DatabaseConnection').getPropertyValue("responseasXML")
log.info res

//To get the total no of records
def count = context.expand('${DatabaseConnection#responseasXML#count(//ResultSet/Row/CUSTOMERDETAILS.FIRSTNAME)}').toInteger()
log.info "count is $count"

//to read values in each row
for(def i=1;i<=count;i++)
{
	log.info "i is $i"
	def firstname = context.expand('${DatabaseConnection#responseasXML#(//ResultSet/Row/CUSTOMERDETAILS.FIRSTNAME)['+i+']}')
	log.info "$firstname"

	def email = "$firstname"+'112@gmail.com'
	log.info "$email"

	def lastname = context.expand('${DatabaseConnection#responseasXML#(//ResultSet/Row/CUSTOMERDETAILS.LASTNAME)['+i+']}')
	log.info "$lastname"
	def dob = context.expand('${DatabaseConnection#responseasXML#(//ResultSet/Row/CUSTOMERDETAILS.DOB)['+i+']}')
	log.info "$dob"

	// get TestCase
	def tc = testRunner.testCase.testSuite.project.testSuites["CustomerRepository Functional Testing"].testCases["CustomerScenarioEnd to End Flow"]

	// set properties from the script itself	
	tc.testSteps["CustomerDetails"].setPropertyValue("email", email )
	tc.testSteps["CustomerDetails"].setPropertyValue("firstname", firstname )
	tc.testSteps["CustomerDetails"].setPropertyValue("lastname", lastname )
	tc.testSteps["CustomerDetails"].setPropertyValue("dob", dob )

	// run test synchrouously
	def runner = tc.run( null, false )

	// show the test case result status
	log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
	log.info "Number of test steps which ran: " + runner.results.size()

	//assertion
	assert runner.status != Status.FAILED : runner.reason
}

